                                  )}

                                  {store.closingTime && (
                                    <div className="flex items-center text-sm text-gray-600">
                                      <Clock className="w-4 h-4 mr-2 text-gray-400" />
                                      <span>Closes at <span className="font-semibold">{store.closingTime}</span></span>
                                    </div>
