// src/pages/OrderPage.jsx
import React, { useState, useEffect, useCallback, useMemo } from "react";
import { motion } from "framer-motion";
import { useParams, useNavigate, Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/components/ui/use-toast";
import { ArrowLeft, PlusCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { calculateMealPrice } from "@/utils/pricingEngine";

const createNewMeal = () => ({
  id: Date.now(),
  mainMeal: "",
  portion: "",
  vegCurries: [],
  nonVegCurries: [],
  gravies: [],
});

const OrderPage = () => {
  const { storeId } = useParams();
  const [store, setStore] = useState(null);
  const [storeSettings, setStoreSettings] = useState(null);
  const [merchantConfig, setMerchantConfig] = useState(null);
  const [meals, setMeals] = useState([createNewMeal()]);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();
  const navigate = useNavigate();

  /** ðŸ”¹ Load merchant + settings from localStorage */
  const loadStore = useCallback(() => {
    const users = JSON.parse(localStorage.getItem("quickmeal_users") || "[]");
    const merchant = users.find(
      (u) => u.id === storeId && u.role === "merchant" && u.approved
    );

    if (!merchant) {
      toast({
        title: "Store Not Found",
        description: "The requested store could not be found",
        variant: "destructive",
      });
      navigate("/customer");
      return;
    }

    const settings = JSON.parse(
      localStorage.getItem("quickmeal_store_settings") || "{}"
    );
    const currentStoreSettings = settings[storeId];

    if (!currentStoreSettings?.isOpen || !currentStoreSettings?.acceptingOrders) {
      toast({
        title: "Store Unavailable",
        description: currentStoreSettings?.isOpen
          ? "Store is currently in busy hours"
          : "Store is currently closed",
        variant: "destructive",
      });
      navigate("/customer");
      return;
    }

    const merchantConfigData = JSON.parse(
      localStorage.getItem("merchantConfig") || "{}"
    );

    setStore(merchant);
    setStoreSettings(currentStoreSettings);
    setMerchantConfig(merchantConfigData);
  }, [storeId, navigate, toast]);

  /** ðŸ”¹ Redirect if not customer */
  useEffect(() => {
    if (!user || user.role !== "customer") {
      navigate("/login");
      return;
    }
    loadStore();
  }, [user, navigate, loadStore]);

  /** ðŸ”¹ Update meal selections */
  const updateMeal = useCallback((index, updatedMealData) => {
    setMeals((currentMeals) => {
      const newMeals = [...currentMeals];
      newMeals[index] = { ...newMeals[index], ...updatedMealData };
      return newMeals;
    });
  }, []);

  /** ðŸ”¹ Calculate meal prices using useMemo to avoid render loop */
  const mealsWithTotals = useMemo(() => {
    if (!storeSettings || !merchantConfig) return meals.map(meal => ({ ...meal, total: 0 }));
    return meals.map((meal) => ({
      ...meal,
      total: calculateMealPrice(meal, { ...storeSettings, ...merchantConfig }),
    }));
  }, [meals, storeSettings, merchantConfig]);

  /** ðŸ”¹ Calculate order total using useMemo */
  const calculatedOrderTotal = useMemo(() => {
    return mealsWithTotals.reduce((sum, meal) => sum + meal.total, 0);
  }, [mealsWithTotals]);

  const addAnotherMeal = () => {
    setMeals((prev) => [...prev, createNewMeal()]);
  };

  const removeMeal = (index) => {
    setMeals((prev) => prev.filter((_, i) => i !== index));
  };

  /** ðŸ”¹ Handle order submission */
  const handleSubmitOrder = async () => {
    for (const meal of mealsWithTotals) {
      if (!meal.mainMeal || !meal.portion) {
        toast({
          title: "Incomplete Order",
          description: "Please select main meal and portion for all meals.",
          variant: "destructive",
        });
        return;
      }
    }

    setLoading(true);
    try {
      const allOrders = JSON.parse(
        localStorage.getItem("quickmeal_orders") || "[]"
      );
      const orderNumber = `QM${Date.now().toString().slice(-6)}`;

      const newOrder = {
        id: Date.now().toString(),
        orderNumber,
        customerId: user.id,
        customerName: user.username,
        storeId: store.id,
        storeName: store.storeName,
        meals: mealsWithTotals,
        total: calculatedOrderTotal,
        status: "pending",
        createdAt: new Date().toISOString(),
      };

      allOrders.push(newOrder);
      localStorage.setItem("quickmeal_orders", JSON.stringify(allOrders));
      toast({
        title: "Order Placed!",
        description: `Your order #${orderNumber} has been submitted successfully`,
      });
      navigate(`/receipt/${newOrder.id}`);
    } catch {
      toast({
        title: "Order Failed",
        description: "Something went wrong. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  if (!store || !storeSettings || !merchantConfig) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading...
      </div>
    );
  }

  /** ðŸ”¹ Config values */
  const mainMeals = merchantConfig.mainMeals || merchantConfig.mains || [];
  const portions = merchantConfig.portions || [];
  const vegCurries = merchantConfig.curries?.filter((c) => c.type === "veg") || [];
  const nonVegCurries =
    merchantConfig.curries?.filter((c) => c.type === "nonveg") || [];
  const gravies = merchantConfig.gravies || [];

  return (
    <div className="min-h-screen p-4">
      <Helmet>
        <title>Order from {store.storeName} - QuickMeal</title>
        <meta
          name="description"
          content={`Place your pre-order at ${store.storeName} and skip the queue.`}
        />
      </Helmet>

      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <Link
            to="/customer"
            className="inline-flex items-center text-orange-600 hover:text-orange-700 mb-4 transition-colors"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Dashboard
          </Link>
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-4xl font-bold gradient-text mb-2">
              {store.storeName}
            </h1>
            <p className="text-gray-600">{store.storeAddress}</p>
          </motion.div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Left: Meals */}
          <div className="lg:col-span-2 space-y-6">
            {mealsWithTotals.map((meal, index) => (
              <div
                key={meal.id}
                className="p-4 border-2 border-dashed rounded-lg space-y-6 relative"
              >
                {meals.length > 1 && (
                  <Button
                    variant="destructive"
                    size="sm"
                    className="absolute top-2 right-2"
                    onClick={() => removeMeal(index)}
                  >
                    Remove Meal
                  </Button>
                )}

                <h2 className="text-2xl font-bold text-gray-700">
                  Meal #{index + 1}
                </h2>

                {/* Main Meals */}
                <div>
                  <h3 className="font-semibold">Main Meal</h3>
                  <div className="grid grid-cols-2 gap-2 mt-2">
                    {mainMeals.map((m) => (
                      <label key={m.id} className="flex items-center gap-2">
                        <input
                          type="radio"
                          name={`mainMeal-${meal.id}`}
                          value={m.id}
                          checked={meal.mainMeal === m.id}
                          onChange={() => updateMeal(index, { mainMeal: m.id })}
                        />
                        <span>
                          {m.name}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Portions */}
                <div>
                  <h3 className="font-semibold">Portion Category</h3>
                  <div className="grid grid-cols-2 gap-2 mt-2">
                    {portions.map((p) => (
                      <label key={p.id} className="flex items-center gap-2">
                        <input
                          type="radio"
                          name={`portion-${meal.id}`}
                          value={p.id}
                          checked={meal.portion === p.id}
                          onChange={() => updateMeal(index, { portion: p.id })}
                        />
                        <span>
                          {p.name}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Veg Curries */}
                <div>
                  <label className="block font-medium">Veg Curries</label>
                  <p className="text-sm text-gray-500">
                    First {merchantConfig?.defaultVegCount || 0} veg curries are
                    free. Extra curries cost ${merchantConfig?.extraVegPrice || 0}{" "}
                    each.
                  </p>
                  <div className="grid grid-cols-2 gap-2 mt-2">
                    {vegCurries.map((curry) => (
                      <label key={curry.id} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={meal.vegCurries.includes(curry.id)}
                          onChange={() => {
                            const selected = meal.vegCurries.includes(curry.id);
                            const updated = selected
                              ? meal.vegCurries.filter((id) => id !== curry.id)
                              : [...meal.vegCurries, curry.id];
                            updateMeal(index, { vegCurries: updated });
                          }}
                        />
                        {curry.name}
                      </label>
                    ))}
                  </div>
                </div>

                {/* Non-Veg Curries */}
                <div>
                  <label className="block font-medium">Non-Veg Curries</label>
                  <div className="space-y-2">
                    {nonVegCurries.map((curry) => {
                      const existing = meal.nonVegCurries.find(
                        (nv) => nv.id === curry.id
                      );
                      return (
                        <div key={curry.id} className="flex items-center gap-2">
                          <input
                            type="checkbox"
                            checked={!!existing}
                            onChange={(e) => {
                              if (e.target.checked) {
                                updateMeal(index, {
                                  nonVegCurries: [
                                    ...meal.nonVegCurries,
                                    curry.divisible 
                                      ? { id: curry.id, pieces: 1 }
                                      : { id: curry.id },
                                  ],
                                });
                              } else {
                                updateMeal(index, {
                                  nonVegCurries: meal.nonVegCurries.filter(
                                    (nv) => nv.id !== curry.id
                                  ),
                                });
                              }
                            }}
                          />
                          <span>{curry.name}</span>
                          {existing && curry.divisible && (
                            <input
                              type="number"
                              min={1}
                              value={existing.pieces}
                              onChange={(e) => {
                                const updated = meal.nonVegCurries.map((nv) =>
                                  nv.id === curry.id
                                    ? {
                                        ...nv,
                                        pieces: parseInt(e.target.value || 1),
                                      }
                                    : nv
                                );
                                updateMeal(index, { nonVegCurries: updated });
                              }}
                              className="border rounded w-16 px-1"
                            />
                          )}
                        </div>
                      );
                    })}
                  </div>
                </div>

                {/* Gravies */}
                <div>
                  <label className="block font-medium">Gravies</label>
                  <div className="grid grid-cols-2 gap-2">
                    {gravies.map((gravy) => (
                      <label key={gravy.id} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={meal.gravies.includes(gravy.id)}
                          onChange={() => {
                            const selected = meal.gravies.includes(gravy.id);
                            const updated = selected
                              ? meal.gravies.filter((id) => id !== gravy.id)
                              : [...meal.gravies, gravy.id];
                            updateMeal(index, { gravies: updated });
                          }}
                        />
                        {gravy.name}
                      </label>
                    ))}
                  </div>
                </div>

                {/* Meal total */}
                <div className="text-right font-bold">
                  Total: ${meal.total.toFixed(2)}
                </div>
              </div>
            ))}

            <Button
              variant="outline"
              onClick={addAnotherMeal}
              className="w-full py-6 text-lg"
            >
              <PlusCircle className="mr-2 h-5 w-5" /> Add Another Meal
            </Button>
          </div>

          {/* Right: Summary */}
          <div className="lg:col-span-1">
            <div className="p-4 border rounded-lg space-y-4">
              <h3 className="text-xl font-bold">Order Summary</h3>
              {mealsWithTotals.map((meal, i) => {
                const main = mainMeals.find((m) => m.id === meal.mainMeal);
                const portion = portions.find((p) => p.id === meal.portion);

                const vegNames = meal.vegCurries
                  .map(
                    (id) => merchantConfig.curries.find((c) => c.id === id)?.name
                  )
                  .filter(Boolean);

                const nonVegNames = meal.nonVegCurries
                  .map((nv) => {
                    const curry = merchantConfig.curries.find(
                      (c) => c.id === nv.id
                    );
                    if (!curry) return null;
                    if (curry.divisible) {
                      return `${curry.name} (${nv.pieces} pcs)`;
                    } else {
                      return nv.extraPieces && nv.extraPieces > 0
                        ? `${curry.name} (+${nv.extraPieces} extra)`
                        : curry.name;
                    }
                  })
                  .filter(Boolean);

                const gravyNames = meal.gravies
                  .map(
                    (id) =>
                      merchantConfig.gravies.find((g) => g.id === id)?.name
                  )
                  .filter(Boolean);

                return (
                  <div key={meal.id} className="border-b pb-2 mb-2">
                    <div className="font-medium">Meal #{i + 1}</div>
                    <div>
                      {main?.name || "No main"} ({portion?.name || "No portion"})
                    </div>
                    {vegNames.length > 0 && (
                      <div>Veg Curries: {vegNames.join(", ")}</div>
                    )}
                    {nonVegNames.length > 0 && (
                      <div>Non-Veg Curries: {nonVegNames.join(", ")}</div>
                    )}
                    {gravyNames.length > 0 && (
                      <div>Gravies: {gravyNames.join(", ")}</div>
                    )}
                    <div className="font-bold">
                      Total: ${meal.total.toFixed(2)}
                    </div>
                  </div>
                );
              })}

              <div className="text-lg font-bold">
                Order Total: ${calculatedOrderTotal.toFixed(2)}
              </div>
              <Button
                onClick={handleSubmitOrder}
                disabled={loading}
                className="w-full py-4 text-lg"
              >
                {loading ? "Placing Order..." : "Place Order"}
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderPage;

src/pages/ReceiptPage.jsx.content:
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useParams, useNavigate, Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/components/ui/use-toast";
import {
  ArrowLeft,
  Receipt,
  Clock,
  CheckCircle,
  MapPin,
  CheckCheck,
} from "lucide-react";

const ReceiptPage = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [store, setStore] = useState(null);
  const [storeSettings, setStoreSettings] = useState(null);
  const { user } = useAuth();
  const { toast } = useToast();
  const navigate = useNavigate();

  const loadOrder = () => {
    const allOrders = JSON.parse(localStorage.getItem("quickmeal_orders") || "[]");
    const foundOrder = allOrders.find(
      (o) => o.id === orderId && o.customerId === user.id
    );

    if (!foundOrder) {
      toast({
        title: "Order Not Found",
        description: "The requested order could not be found",
        variant: "destructive",
      });
      navigate("/customer");
      return;
    }

    const users = JSON.parse(localStorage.getItem("quickmeal_users") || "[]");
    const merchant = users.find((u) => u.id === foundOrder.storeId);

    const settings = JSON.parse(
      localStorage.getItem("quickmeal_store_settings") || "{}"
    );
    const currentStoreSettings = settings[foundOrder.storeId];

    setOrder(foundOrder);
    setStore(merchant);
    setStoreSettings(currentStoreSettings);
  };

  useEffect(() => {
    if (!user || user.role !== "customer") {
      navigate("/login");
      return;
    }
    loadOrder();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [orderId, user, navigate]);

  const handleMarkAsDone = () => {
    const allOrders = JSON.parse(localStorage.getItem("quickmeal_orders") || "[]");
    const updatedOrders = allOrders.map((o) =>
      o.id === orderId ? { ...o, status: "collected" } : o
    );
    localStorage.setItem("quickmeal_orders", JSON.stringify(updatedOrders));
    loadOrder();
    toast({
      title: "Order Collected!",
      description: "Thank you for confirming. Enjoy your meal!",
    });
  };

  const getStatusInfo = (status) => {
    const statusMap = {
      pending: {
        label: "Order Received",
        color: "secondary",
        description:
          "Your order has been received and is waiting to be prepared",
        icon: Clock,
      },
      preparing: {
        label: "Preparing",
        color: "default",
        description: "Your meal is being prepared with care",
        icon: Clock,
      },
      ready: {
        label: "Ready for Pickup",
        color: "default",
        description:
          "Your order is ready! Please come to the store to collect it",
        icon: CheckCircle,
      },
      completed: {
        label: "Completed",
        color: "default",
        description: "Your order is ready for pickup!",
        icon: CheckCircle,
      },
      collected: {
        label: "Collected",
        color: "success",
        description: "You have collected your order. Enjoy!",
        icon: CheckCheck,
      },
    };
    return statusMap[status] || statusMap.pending;
  };

  if (!order || !store) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading...
      </div>
    );
  }

  const statusInfo = getStatusInfo(order.status);
  const StatusIcon = statusInfo.icon;
  const isMultiMeal = Array.isArray(order.meals);

  const getNameById = (list, id) =>
    list?.find((item) => item.id === id)?.name || id;

  return (
    <div className="min-h-screen p-4">
      <Helmet>
        <title>Order Receipt #{order.orderNumber} - QuickMeal</title>
        <meta
          name="description"
          content={`Your order receipt from ${store.storeName}. Track your meal preparation status.`}
        />
      </Helmet>

      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <Link
            to="/customer"
            className="inline-flex items-center text-orange-600 hover:text-orange-700 mb-4 transition-colors"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Dashboard
          </Link>
        </div>

        {/* Receipt Card */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Card className="order-card shadow-2xl">
            <CardHeader className="text-center border-b">
              <div className="flex justify-center mb-4">
                <Receipt className="w-16 h-16 text-orange-500" />
              </div>
              <CardTitle className="text-3xl font-bold gradient-text">
                Order Receipt
              </CardTitle>
              <CardDescription className="text-lg">
                Order #{order.orderNumber}
              </CardDescription>
            </CardHeader>

            <CardContent className="space-y-6 p-8">
              {/* Status */}
              <div className="text-center p-6 bg-gradient-to-r from-orange-50 to-amber-50 rounded-lg">
                <StatusIcon className="w-12 h-12 mx-auto mb-3 text-orange-500" />
                <Badge
                  variant={statusInfo.color}
                  className="text-lg px-4 py-2 mb-3"
                >
                  {statusInfo.label}
                </Badge>
                <p className="text-gray-600">{statusInfo.description}</p>
              </div>

              {/* Store Information */}
              <div className="border-b pb-6">
                <h3 className="text-xl font-bold mb-3 text-gray-800">
                  Restaurant Details
                </h3>
                <div className="space-y-2">
                  <p className="font-semibold text-lg">{store.storeName}</p>
                  <div className="flex items-center text-gray-600">
                    <MapPin className="w-4 h-4 mr-2" />
                    {store.storeAddress}
                  </div>
                </div>
              </div>

              {/* Order Details */}
              <div className="border-b pb-6">
                <h3 className="text-xl font-bold mb-3 text-gray-800">
                  Order Details
                </h3>
                {isMultiMeal ? (
                  order.meals.map((meal, index) => (
                    <div
                      key={index}
                      className="space-y-2 mb-4 pb-4 border-b last:border-b-0 last:pb-0 last:mb-0"
                    >
                      <h4 className="font-bold">Meal #{index + 1}</h4>
                      <div>
                        <strong>Main Meal:</strong>{" "}
                        {getNameById(storeSettings?.mainMeals, meal.mainMeal)}
                      </div>
                      <div>
                        <strong>Portion:</strong>{" "}
                        {getNameById(
                          storeSettings?.portionCategories,
                          meal.portion
                        )}
                      </div>
                      <div>
                        <strong>Veg Curries:</strong>{" "}
                        {meal.vegCurries.length > 0
                          ? meal.vegCurries
                              .map((id) =>
                                getNameById(storeSettings?.curries, id)
                              )
                              .join(", ")
                          : "None"}
                      </div>
                      <div>
                        <strong>Non-Veg Curries:</strong>{" "}
                        {meal.nonVegCurries.length > 0
                          ? meal.nonVegCurries
                              .map((nv) => {
                                const curry = storeSettings?.curries?.find(
                                  (c) => c.id === nv.id
                                );
                                if (!curry) return nv.id;
                                if (curry.divisible) {
                                  return `${curry.name} (${nv.pieces} pcs)`;
                                } else {
                                  return nv.extraPieces && nv.extraPieces > 0
                                    ? `${curry.name} (+${nv.extraPieces} extra)`
                                    : curry.name;
                                }
                              })
                              .join(", ")
                          : "None"}
                      </div>
                      <div>
                        <strong>Gravies:</strong>{" "}
                        {meal.gravies.length > 0
                          ? meal.gravies
                              .map((id) =>
                                getNameById(storeSettings?.gravies, id)
                              )
                              .join(", ")
                          : "None"}
                      </div>
                      <div className="flex justify-end font-semibold pt-2">
                        <span>Subtotal: ${meal.total.toFixed(2)}</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="space-y-2">
                    <div>
                      <strong>Main Meal:</strong>{" "}
                      {getNameById(storeSettings?.mainMeals, order.mainMeal)}
                    </div>
                    <div>
                      <strong>Portion:</strong>{" "}
                      {getNameById(storeSettings?.portionCategories, order.portion)}
                    </div>
                    <div>
                      <strong>Veg Curries:</strong>{" "}
                      {order.vegCurries?.length > 0
                        ? order.vegCurries
                            .map((id) => getNameById(storeSettings?.curries, id))
                            .join(", ")
                        : "None"}
                    </div>
                    <div>
                      <strong>Non-Veg Curries:</strong>{" "}
                      {order.nonVegCurries?.length > 0
                        ? order.nonVegCurries
                            .map((nv) => {
                              const curry = storeSettings?.curries?.find(
                                (c) => c.id === nv.id
                              );
                              if (!curry) return nv.id;
                              if (curry.divisible) {
                                return `${curry.name} (${nv.pieces} pcs)`;
                              } else {
                                return nv.extraPieces && nv.extraPieces > 0
                                  ? `${curry.name} (+${nv.extraPieces} extra)`
                                  : curry.name;
                              }
                            })
                            .join(", ")
                        : "None"}
                    </div>
                    <div>
                      <strong>Gravies:</strong>{" "}
                      {order.gravies?.length > 0
                        ? order.gravies
                            .map((id) => getNameById(storeSettings?.gravies, id))
                            .join(", ")
                        : "None"}
                    </div>
                  </div>
                )}
              </div>

              {/* Payment Summary */}
              <div className="border-b pb-6">
                <h3 className="text-xl font-bold mb-3 text-gray-800">
                  Payment Summary
                </h3>
                <div className="space-y-2">
                  <div className="flex justify-between text-2xl font-bold text-orange-600">
                    <span>Total Paid:</span>
                    <span>${order.total.toFixed(2)}</span>
                  </div>
                  <p className="text-sm text-gray-500">
                    Payment processed successfully
                  </p>
                </div>
              </div>

              {/* Order Information */}
              <div className="text-sm text-gray-600 space-y-1">
                <p>
                  <strong>Order Date:</strong>{" "}
                  {new Date(order.createdAt).toLocaleString()}
                </p>
                <p>
                  <strong>Customer:</strong> {order.customerName}
                </p>
                <p>
                  <strong>Status:</strong> {statusInfo.label}
                </p>
              </div>

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 pt-6">
                {order.status === "completed" ? (
                  <Button
                    onClick={handleMarkAsDone}
                    className="flex-1 bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white"
                  >
                    <CheckCheck className="w-5 h-5 mr-2" /> Mark as Done
                  </Button>
                ) : (
                  <Link to="/customer" className="flex-1">
                    <Button
                      variant="outline"
                      className="w-full border-orange-300 text-orange-600 hover:bg-orange-50"
                    >
                      Back to Dashboard
                    </Button>
                  </Link>
                )}
                <Button
                  onClick={() => window.print()}
                  className="flex-1 bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 text-white"
                >
                  Print Receipt
                </Button>
              </div>

              {/* Important Notes */}
              <div className="bg-blue-50 p-4 rounded-lg">
                <h4 className="font-semibold text-blue-800 mb-2">
                  Important Notes:
                </h4>
                <ul className="text-sm text-blue-700 space-y-1">
                  <li>â€¢ Please bring this receipt when collecting your order</li>
                  <li>â€¢ Orders are typically ready within 15-20 minutes</li>
                  <li>â€¢ Contact the restaurant if you have any questions</li>
                </ul>
              </div>

              {order.status === "ready" && (
                <div className="bg-green-50 p-4 rounded-lg text-center">
                  <h4 className="font-bold text-green-800 text-lg mb-2">
                    ðŸŽ‰ Your Order is Ready!
                  </h4>
                  <p className="text-green-700">
                    Please visit the restaurant to collect your delicious meal!
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </motion.div>
      </div>
    </div>
  );
};

export default ReceiptPage;

